import React from 'react';
import {
    StyleSheet,
    View,
    FlatList,
} from 'react-native'

import { MenuButton } from '../components/GameScreen/Buttons/MenuButton.js';
import { UserActionButton } from '../components/GameScreen/Buttons/UserActionButton.js';
import { SolutionContainer } from '../components/GameScreen/Containers/SolutionContainer.js';
import { PoolContainer } from '../components/GameScreen/Containers/PoolContainer.js';
import { FlatListContainer } from '../components/GameScreen/Containers/FlatListContainer.js';

const flatListData = null

let GameScreen = ({ difficulty }) => {
    return (
        <View style={styles.screen}>
            <View style={styles.topSection}>
                <MenuButton />
                <SolutionContainer solution={null} hidden={false} />
            </View>
            <View style={styles.midSection}>
                <FlatList
                    data={flatListData}
                    keyExtractor={(index) => index.guid}
                    renderItem={({ item }) => (
                        <FlatListContainer />
                    )}
                />
            </View>
            <View style={styles.bottomSection}>
                <PoolContainer numPegs={4} availableColors={["red", "blue", "yellow", "green"]} />
                <View style={styles.buttonBar}>
                    <UserActionButton />
                    <UserActionButton />
                </View>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    screen: {
        flex: 1,
        alignContent: 'center',
        margin: 5,
    },
    topSection: {
        flex: 0.6,
        flexDirection: 'row',
        justifyContent: 'center',
        backgroundColor: 'red',
    },
    midSection: {
        flex: 4,
        flexDirection: 'row',
        alignContent: 'center',
        backgroundColor: 'yellow',
    },
    bottomSection: {
        flex: 1.25,
        alignContent: 'center',
        backgroundColor: 'blue',
    },
    buttonBar: {
        flex: 0.4,
        flexDirection: 'row',
        alignContent: 'center',
        backgroundColor: 'red',
    }
});

export default GameScreen
