'use-strict';

import React from 'react';
import { View, StyleSheet } from 'react-native';

export function PoolContainer({ numPegs, availableColors }) {
    return (
        <View style={styles.container}>
            <View style={styles.colorChoices}>
                {availableColors.map((color, key) =>
                    <View style={{ flex: 1, borderRadius: 100, aspectRatio: 1, maxHeight: 30, maxWidth: 30, marginLeft: 2, borderColor: 'black', borderWidth: 1, backgroundColor: color }} key={key} />
                )}
            </View>
            <View style={styles.currentGuess}>
                {Array.from({ length: numPegs }, (_, key) =>
                    <View style={{ borderRadius: 100, height: 30, width: 30, borderColor: 'black', borderWidth: 1, backgroundColor: 'white' }} key={key} />
                )}
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        margin: 5,
        flexDirection: 'row',
    },
    colorChoices: {
        flex: 1.5,
        flexDirection: 'row',
        flexWrap: 'wrap',
        backgroundColor: 'green',
        alignItems: 'stretch',
        alignContent: 'stretch',
        backgroundColor: 'red',
    },
    currentGuess: {
        flex: 4,
        flexDirection: 'row',
        backgroundColor: 'green',
        alignItems: 'center',
        justifyContent: 'space-around',
    }
});
